mapper.select:
SELECT t.* from t_wxcms_msg_news t , t_wxcms_msg_base b
WHERE b.inputCode like CONCAT('%','${param1}','%' ) and t.base_id = b.id 
ORDER BY RAND() LIMIT #{param2}

SELECT t.* from t_wxcms_msg_news t , t_wxcms_msg_base b
WHERE t.base_id = b.id 
ORDER BY RAND() LIMIT #{num}

mapper.update:
UPDATE t_wxcms_msg_news 
SET
   media_id = #{mediaId} 
WHERE ID = #{id}
